INSERT INTO `modx_site_plugins` VALUES (26,2,0,'MinifyHTML','',0,0,0,'$output = $modx->resource->_output;\n$output= preg_replace(\'|\\s+|\', \' \', $output);\n$modx->resource->_output = $output;',0,'a:0:{}',0,'',0,''),(25,0,0,'phpThumbOfCacheManager','Handles cache cleaning when clearing the Site Cache.',0,100,0,'/*\n * Handles cache cleanup\n * pThumb\n * Copyright 2013 Jason Grant\n *\n * Please see the GitHub page for documentation or to report bugs:\n * https://github.com/oo12/phpThumbOf\n *\n * pThumb is free software; you can redistribute it and/or modify it\n * under the terms of the GNU General Public License as published by the Free\n * Software Foundation; either version 2 of the License, or (at your option) any\n * later version.\n *\n * pThumb is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * phpThumbOf; if not, write to the Free Software Foundation, Inc., 59 Temple\n * Place, Suite 330, Boston, MA 02111-1307 USA\n */\n\nif ($modx->event->name === \'OnSiteRefresh\') {\n	if (!$modx->loadClass(\'pThumbCacheCleaner\', MODX_CORE_PATH . \'components/phpthumbof/model/\', true, true)) {\n		$modx->log(modX::LOG_LEVEL_ERROR, \'[pThumb] Could not load pThumbCacheCleaner class.\');\n		return;\n	}\n	static $pt_settings = array();\n	$pThumb = new pThumbCacheCleaner($modx, $pt_settings, array(), true);\n	$pThumb->cleanCache();\n}',0,NULL,0,'',0,''),(9,0,0,'Redactor','Redactor WYSIWYG editor plugin for MODX Revolution',0,0,0,'/**\n * Redactor WYSIWYG Editor Plugin\n *\n * Events: OnManagerPageBeforeRender, OnRichTextEditorRegister, OnRichTextBrowserInit, OnDocFormPrerender\n *\n * @author JP DeVries <mail@devries.jp>\n *\n * @package redactor\n */\n\n$corePath = $modx->getOption(\'redactor.core_path\', null, $modx->getOption(\'core_path\').\'components/redactor/\');\n\nswitch ($modx->event->name) {\n    case \'OnTVInputRenderList\':\n        $modx->event->output($corePath.\'elements/tvs/input/\');\n        break;\n\n    case \'OnTVInputPropertiesList\':\n        $modx->event->output($corePath.\'elements/tvs/inputoptions/\'); \n        break;\n\n    case \'OnTVOutputRenderPropertiesList\':\n        $modx->event->output($corePath.\'elements/tvs/properties/\');\n        break;\n\n    case \'OnManagerPageBeforeRender\':\n        break;\n\n    case \'OnRichTextEditorRegister\':\n        $modx->event->output(\'Redactor\');\n        break;\n\n    case \'OnFileManagerFileRename\':\n        /**\n         * @var string $path\n         */\n        $redactor = $modx->getService(\'redactor\', \'Redactor\', $corePath . \'model/redactor/\');\n        if (!($redactor instanceof Redactor)) {\n            $modx->log(modX::LOG_LEVEL_ERROR, \'[Redactor] Error loading Redactor service class.\');\n            return;\n        }\n        $redactor->renames[] = $path;\n\n        break;\n\n    case \'OnRichTextEditorInit\':\n        /**\n         * @var string $editor\n         * @var array $elements\n         *\n         * Only load up the editor if the editor is Redactor, and use_editor is enabled.\n         */\n        $rte = isset($editor) ? $editor : $modx->getOption(\'which_editor\', null, \'\');\n        if ($rte !== \'Redactor\' || !$modx->getOption(\'use_editor\', null, true)) {\n            return;\n        }\n\n        /**\n         * Attempt to load the Redactor service class. Log error and halt processing if it fails.\n         */\n        $redactor = $modx->getService(\'redactor\', \'Redactor\', $corePath . \'model/redactor/\');\n        if (!($redactor instanceof Redactor)) {\n            $modx->log(modX::LOG_LEVEL_ERROR, \'[Redactor] Error loading Redactor service class.\');\n            return;\n        }\n\n        if (isset($resource) && $resource instanceof modResource) {\n            $redactor->setResource($resource);\n        }\n        elseif ($modx->resource) {\n            $redactor->setResource($modx->resource);\n        }\n        elseif ($modx->controller && isset($modx->controller->resource) && $modx->controller->resource instanceof modResource) {\n            $redactor->setResource($modx->controller->resource);\n        }\n\n        $customCss = $redactor->getOption(\'redactor.css\');\n\n        if ($modx->controller && !($modx->controller instanceof modManagerControllerDeprecated)) {\n            $modx->controller->addLexiconTopic(\'redactor:default\');\n            $modx->controller->addCss($redactor->config[\'assetsUrl\'].\'redactor-2.3.1.min.css\');\n            if ($redactor->degradeUI) $modx->controller->addCss($redactor->config[\'assetsUrl\'].\'buttons-legacy.min.css\');\n            if ($redactor->rebeccaDay) $modx->controller->addCss($redactor->config[\'assetsUrl\'].\'rebecca.min.css\');\n            if ($customCss) $modx->controller->addCss($customCss);\n        }\n        else {\n            $modx->lexicon->load(\'redactor:default\');\n            $modx->regClientCSS($redactor->config[\'assetsUrl\'].\'redactor-2.3.1.min.css\');\n            if($redactor->degradeUI) $modx->regClientCSS($redactor->config[\'assetsUrl\'].\'buttons-legacy.min.css\');\n            if($redactor->rebeccaDay) $modx->regClientCSS($redactor->config[\'assetsUrl\'].\'rebecca.min.css\');\n            if($customCss) $modx->regClientCSS($customCss);\n        }\n\n        $html = $redactor->getHtml();\n        $modx->event->output($html);\n        break;\n}\n\nreturn;',0,NULL,0,'',0,''),(7,0,0,'Ace','Ace code editor plugin for MODx Revolution',0,0,0,'/**\n * Ace Source Editor Plugin\n *\n * Events: OnManagerPageBeforeRender, OnRichTextEditorRegister, OnSnipFormPrerender,\n * OnTempFormPrerender, OnChunkFormPrerender, OnPluginFormPrerender,\n * OnFileCreateFormPrerender, OnFileEditFormPrerender, OnDocFormPrerender\n *\n * @author Danil Kostin <danya.postfactum(at)gmail.com>\n *\n * @package ace\n *\n * @var array $scriptProperties\n * @var Ace $ace\n */\nif ($modx->event->name == \'OnRichTextEditorRegister\') {\n    $modx->event->output(\'Ace\');\n    return;\n}\n\nif ($modx->getOption(\'which_element_editor\', null, \'Ace\') !== \'Ace\') {\n    return;\n}\n\n$ace = $modx->getService(\'ace\', \'Ace\', $modx->getOption(\'ace.core_path\', null, $modx->getOption(\'core_path\').\'components/ace/\').\'model/ace/\');\n$ace->initialize();\n\n$extensionMap = array(\n    \'tpl\'   => \'text/x-smarty\',\n    \'htm\'   => \'text/html\',\n    \'html\'  => \'text/html\',\n    \'css\'   => \'text/css\',\n    \'scss\'  => \'text/x-scss\',\n    \'less\'  => \'text/x-less\',\n    \'svg\'   => \'image/svg+xml\',\n    \'xml\'   => \'application/xml\',\n    \'xsl\'   => \'application/xml\',\n    \'js\'    => \'application/javascript\',\n    \'json\'  => \'application/json\',\n    \'php\'   => \'application/x-php\',\n    \'sql\'   => \'text/x-sql\',\n    \'md\'    => \'text/x-markdown\',\n    \'txt\'   => \'text/plain\',\n    \'twig\'  => \'text/x-twig\'\n);\n\n// Define default mime for html elements(templates, chunks and html resources)\n$html_elements_mime=$modx->getOption(\'ace.html_elements_mime\',null,false);\nif(!$html_elements_mime){\n    // this may deprecated in future because components may set ace.html_elements_mime option now\n    switch (true) {\n        case $modx->getOption(\'twiggy_class\'):\n            $html_elements_mime = \'text/x-twig\';\n            break;\n        case $modx->getOption(\'pdotools_fenom_parser\'):\n            $html_elements_mime = \'text/x-smarty\';\n            break;\n        default:\n            $html_elements_mime = \'text/html\';\n    }\n}\n\n// Defines wether we should highlight modx tags\n$modxTags = false;\nswitch ($modx->event->name) {\n    case \'OnSnipFormPrerender\':\n        $field = \'modx-snippet-snippet\';\n        $mimeType = \'application/x-php\';\n        break;\n    case \'OnTempFormPrerender\':\n        $field = \'modx-template-content\';\n        $modxTags = true;\n        $mimeType = $html_elements_mime;\n        break;\n    case \'OnChunkFormPrerender\':\n        $field = \'modx-chunk-snippet\';\n        if ($modx->controller->chunk && $modx->controller->chunk->isStatic()) {\n            $extension = pathinfo($modx->controller->chunk->name, PATHINFO_EXTENSION);\n            if(!$extension||!isset($extensionMap[$extension])){\n                $extension = pathinfo($modx->controller->chunk->getSourceFile(), PATHINFO_EXTENSION);\n            }\n            $mimeType = isset($extensionMap[$extension]) ? $extensionMap[$extension] : \'text/plain\';\n        } else {\n            $mimeType = $html_elements_mime;\n        }\n        $modxTags = true;\n        break;\n    case \'OnPluginFormPrerender\':\n        $field = \'modx-plugin-plugincode\';\n        $mimeType = \'application/x-php\';\n        break;\n    case \'OnFileCreateFormPrerender\':\n        $field = \'modx-file-content\';\n        $mimeType = \'text/plain\';\n        break;\n    case \'OnFileEditFormPrerender\':\n        $field = \'modx-file-content\';\n        $extension = pathinfo($scriptProperties[\'file\'], PATHINFO_EXTENSION);\n        $mimeType = isset($extensionMap[$extension])\n            ? $extensionMap[$extension]\n            : \'text/plain\';\n        $modxTags = $extension == \'tpl\';\n        break;\n    case \'OnDocFormPrerender\':\n        if (!$modx->controller->resourceArray) {\n            return;\n        }\n        $field = \'ta\';\n        $mimeType = $modx->getObject(\'modContentType\', $modx->controller->resourceArray[\'content_type\'])->get(\'mime_type\');\n\n        if($mimeType == \'text/html\')$mimeType = $html_elements_mime;\n\n        if ($modx->getOption(\'use_editor\')){\n            $richText = $modx->controller->resourceArray[\'richtext\'];\n            $classKey = $modx->controller->resourceArray[\'class_key\'];\n            if ($richText || in_array($classKey, array(\'modStaticResource\',\'modSymLink\',\'modWebLink\',\'modXMLRPCResource\'))) {\n                $field = false;\n            }\n        }\n        $modxTags = true;\n        break;\n    default:\n        return;\n}\n\n$modxTags = (int) $modxTags;\n$script = \'\';\nif ($field) {\n    $script .= \"MODx.ux.Ace.replaceComponent(\'$field\', \'$mimeType\', $modxTags);\";\n}\n\nif ($modx->event->name == \'OnDocFormPrerender\' && !$modx->getOption(\'use_editor\')) {\n    $script .= \"MODx.ux.Ace.replaceTextAreas(Ext.query(\'.modx-richtext\'));\";\n}\n\nif ($script) {\n    $modx->controller->addHtml(\'<script>Ext.onReady(function() {\' . $script . \'});</script>\');\n}',0,'a:0:{}',0,'',0,'ace/elements/plugins/ace.plugin.php'),(10,0,0,'ContentBlocks','The main plugin for ContentBlocks, responsible for handling generating the form as well as saving the resource. (Part of ContentBlocks)',0,5,0,'/**\n * ContentBlocks\n *\n * Events: OnDocFormRender, OnDocFormSave\n *\n * @package contentblocks\n */\n\n$corePath = $modx->getOption(\'contentblocks.core_path\', null, $modx->getOption(\'core_path\') . \'components/contentblocks/\');\n$assetsUrl = $modx->getOption(\'contentblocks.assets_url\', null, $modx->getOption(\'assets_url\') . \'components/contentblocks/\');\n\n/**\n * @var ContentBlocks $ContentBlocks\n * @var modResource $resource\n * @var modX $modx\n * @var array $scriptProperties\n */\n$ContentBlocks = $modx->getService(\'contentblocks\', \'ContentBlocks\', $corePath . \'model/contentblocks/\');\n\nswitch ($modx->event->name) {\n    case \'OnDocFormPrerender\':\n        if ($modx->controller && isset($modx->controller->resource) && $modx->controller->resource instanceof modResource) {\n            $resource = $modx->controller->resource;\n            $ContentBlocks->setResource($modx->controller->resource);\n        }\n        else {\n            return;\n        }\n\n        // Default settings\n        $disabled = (int)$modx->getOption(\'contentblocks.disabled\', null, false);\n        $acceptedResourceTypes = $modx->getOption(\'contentblocks.accepted_resource_types\', null, \'modDocument,mgResource\');\n\n        // Fake the wctx variable for loading the working context to get settings\n        if (!isset($_GET[\'wctx\'])) $_GET[\'wctx\'] = $resource->get(\'context_key\');\n\n        // If we got the working context, get some settings\n        if ($modx->controller->loadWorkingContext()) {\n            $disabled = (int)$modx->controller->workingContext->getOption(\'contentblocks.disabled\', $disabled);\n            $acceptedResourceTypes = $modx->controller->workingContext->getOption(\'contentblocks.accepted_resource_types\', $acceptedResourceTypes);\n        }\n\n        // Check if we\'re using an allowed resource type\n        $acceptedType = false;\n        $acceptedResourceTypes = explode(\',\', $acceptedResourceTypes);\n        foreach ($acceptedResourceTypes as $type) {\n            if ($resource instanceof $type) $acceptedType = true;\n        }\n\n        // If contentblocks is disabled or this is not an accepted resource, we can stop here.\n        if ($disabled || !$acceptedType) return;\n\n        // Load the lexicon\n        $modx->controller->addLexiconTopic(\'contentblocks:default\');\n        $isContentBlocks = $resource->getProperty(\'_isContentBlocks\', \'contentblocks\', null);\n\n        // Load the use_contentblocks setting\n        if ($ContentBlocks->getOption(\'contentblocks.show_resource_option\', null, true)) {\n            $modx->controller->addJavascript($assetsUrl . \'cmp/js/widgets/booleancombo.js\');\n            $settingChunk = $modx->newObject(\'modChunk\', array(\n                \'content\' => file_get_contents($corePath . \'templates/setting.tpl\')\n            ));\n\n            $modx->controller->addHtml($settingChunk->process(array(\n                \'value\' => ($isContentBlocks !== false) ? \'1\' : \'0\'\n            )));\n        }\n\n        // Halt if we\'re not using contentblocks here\n        if ($isContentBlocks === false) {\n            // we\'re done here\n            return;\n        }\n\n        // Prepare the content\n        $contents = array();\n        if ($isContentBlocks) {\n            // When reloading, the contentblocks field will contain the JSON data\n            $contents = $resource->get(\'contentblocks\');\n            if (empty($contents)) {\n                // If it\'s empty, we get the data from the resource property instead\n                $contents = $resource->getProperty(\'content\', \'contentblocks\');\n            }\n\n            // Try to decode the JSON\n            $contents = $modx->fromJSON($contents);\n        }\n\n        if (!is_array($contents) || empty($contents)) {\n            $content = $resource->get(\'content\');\n            $contents = $ContentBlocks->getDefaultCanvas(false, $content);\n        }\n\n        // Generate a wrapper class to apply, so we can target stuff in CSS\n        $wrapperCls = array();\n        $wrapperCls[] = \'type_\' . strtolower($resource->get(\'class_key\'));\n        $wrapperCls[] = \'position_\' . $ContentBlocks->config[\'canvas_position\'];\n        $modxVersion = $modx->getVersionData();\n        if (version_compare($modxVersion[\'full_version\'], \'2.3.0-dev\', \'>=\')) {\n            $wrapperCls[] = \'modx_v23\';\n        }\n        if (version_compare($modxVersion[\'full_version\'], \'3.0.0-dev\', \'>=\')) {\n            $wrapperCls[] = \'modx_v30\';\n        }\n        $wrapperCls = implode(\' \', $wrapperCls);\n\n        // Grab objects for building the canvas\n        $objects = $ContentBlocks->getObjectsForCanvas($resource);\n        $categories = $modx->toJSON($objects[\'categories\']);\n        $fields = $modx->toJSON($objects[\'fields\']);\n        $layouts = $modx->toJSON($objects[\'layouts\']);\n        $templates = $modx->toJSON($objects[\'templates\']);\n\n        $contents = $modx->toJSON($contents);\n        $resourceInfo = $modx->toJSON($resource->get(array(\'id\', \'pagetitle\', \'context_key\')));\n        $config = $modx->toJSON($ContentBlocks->config);\n\n        $modx->controller->addHtml(<<<HTML\n<script type=\"text/javascript\">\n    var ContentBlocksCategories = $categories,\n        ContentBlocksFields = $fields,\n        ContentBlocksLayouts = $layouts,\n        ContentBlocksTemplates = $templates,\n        ContentBlocksContents = $contents,\n        ContentBlocksConfig = $config,\n        ContentBlocksWrapperCls = \"$wrapperCls\",\n        ContentBlocksResource = $resourceInfo;\n\n    var cbGenerated = false;\n    MODx.on(\'ready\', function () {\n        // Prevent double-generation\n        if (cbGenerated) return;\n        cbGenerated = true;\n\n        ContentBlocks.render();\n\n        // Hook up to beforesubmit to fetch the values and fetch the content blocks\n        Ext.getCmp(\'modx-panel-resource\').on(\'beforesubmit\', function(o) {\n            o.form.baseParams[\'contentblocks\'] = ContentBlocks.getData();\n        });\n    });\n</script>\nHTML\n        );\n        $scriptTags = $ContentBlocks->getAssets();\n        $modx->controller->addHtml($scriptTags);\n\n        break;\n\n    case \'OnDocFormSave\':\n        $ContentBlocks->setResource($resource);\n        $modx->resource = $resource;\n\n        $cbJson = $resource->get(\'contentblocks\');\n\n        $cbContent = $modx->fromJSON($cbJson);\n\n        // RenderContent Event\n        $response = $modx->invokeEvent(\'ContentBlocks_RenderContent\', array(\n            \'cbContent\' => $cbContent,\n            \'cbJson\' => $cbJson,\n            \'resource\' => $resource\n        ));\n        // check if customized content was returned\n        if (!empty($response) && is_array($response) && json_encode($response) !== \'[\"\"]\') {\n            $cbContent = $response[0][\'cbContent\'];\n            $cbJson = $response[0][\'cbJson\'];\n        }\n\n        if (!empty($cbJson) && $cbContent !== false && is_array($cbContent)) {\n            $summary = $ContentBlocks->summarizeContent($cbContent);\n            $resource->setProperties(array(\n                \'content\' => $cbJson,\n                \'linear\' => $summary[\'linear\'],\n                \'fieldcounts\' => $summary[\'fieldcounts\'],\n                \'_isContentBlocks\' => true,\n            ), \'contentblocks\', true);\n\n            // We save the CB data as soon as possible ...\n            $resource->save();\n            // ... then we parse it to HTML which is stored in the content ...\n            try {\n                $ContentBlocks->loadParser();\n                $resource->setContent($ContentBlocks->generateHtml($cbContent));\n                $ContentBlocks->restoreParser();\n            } catch (Exception $e) {\n                $modx->log(modX::LOG_LEVEL_ERROR, \'Exception while trying to parse the content of resource \' . $resource->id . \': \' . $e->getMessage());\n            }\n            // ... to make sure parse errors don\'t lose the content.\n        }\n        $resource->set(\'contentblocks\', \'\');\n\n        // Make sure we need to continue to use contentblocks\n        $useCb = $resource->get(\'use_contentblocks\');\n        if (in_array($useCb, array(\'0\', \'1\'), true)) {\n            $resource->setProperty(\'_isContentBlocks\', (bool)$useCb, \'contentblocks\');\n        }\n        $resource->save();\n        break;\n\n    /**\n     * @var string $path\n     */\n    case \'OnFileManagerFileRename\':\n        $ContentBlocks->renames[] = $path;\n        break;\n}\n\nreturn;',0,NULL,0,'',0,''),(4,0,0,'Updater Cache Observer','This plugin observes package changes and refreshes Updaters caches accordingly.',0,2,0,'$eventName = $modx->event->name;\nswitch($eventName) {\n\n    case \'OnCacheUpdate\':\n\n        /* only trigger a refresh if packages or system settings are changed */\n        $packageKey = $modx->getOption(\'cache_packages_key\', null, \'packages\');\n        $settingsKey 